mapper.select:
select *
from comment
where post_num=#{postNum} and post_type=#{postType}
order by parent_num, group_order;

select max(group_order)+1
from comment c
where parent_num=#{parentNum}

mapper.update:
update comment
set
comment_content=#{commentContent}
where comment_num=#{commentNum}

mapper.select:
select count(*)
from comment
where post_num=#{postNum} and post_type=#{postType}
