mapper.update:
update board
set likes = likes+1
where board_num = #{boardNum}

update board
set likes = likes-1
where board_num = #{boardNum}

mapper.select:
select if(ifnull(count(like_check), false), true, false) as likeCheck
from like_table
where board_num = #{boardNum} and userid=#{userId};

select likes
from board
where board_num = #{boardNum}
